import React, { useState } from 'react';

function Calendar() {
  const [email, setEmail] = useState('');
  const [calendarSource, setCalendarSource] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  // Source: https://jsfiddle.net/joshuatz/hq9m05tL/
  const showCal = () => {
    if (email && /.+\@.+/.test(email)) {
      // TO-DO: Pull email from user on login
      setCalendarSrc(`https://calendar.google.com/calendar/embed?src=${encodeURIComponent(email)}`);
      setShowError(false);
    } else {
      // SOME ERROR HANDLING TO ADD HERE!
    }
  };

  return (
    <div>
      <h2>Embed Google Calendar</h2>

      {/* Email input */}
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
        placeholder="Enter Google email address"
      />
      <button onClick={showCal}>Show Calendar</button>

      {/* Error message */}
      {showError && <div style={{ color: 'red', fontWeight: 'bold' }}>That doesn't look like a valid email...</div>}

      {/* Google Calendar iframe */}
      {calendarSrc && (
        <iframe
          src={calendarSrc}
          width="800"
          height="600"
          style={{ marginTop: '20px' }}
          title="Google Calendar"
        ></iframe>
      )}
    </div>
  );
}

export default GoogleCalendarEmbed;
