import React, { useState } from 'react';
import './Profile.css';

// ProfileSection component for collapsible dropdown with text input
function ProfileSection({ title, children }) {
  const [open, setOpen] = useState(false);

  return (
    <div className="profile-section">
      <div className="profile-section-header" onClick={() => setOpen(!open)}>
        <span>{title}</span>
        <span className={`arrow ${open ? 'open' : ''}`}>&#9662;</span> {/* â–¼ icon */}
      </div>
      {open && <div className="profile-section-content">{children}</div>}
    </div>
  );
}

// Main Profile component
function Profile() {
  // State to handle input changes
  const [profileName, setProfileName] = useState('');
  const [email, setEmail] = useState('');
  const [major, setMajor] = useState('');

  return (
    <div className="profile-setting">
      <div className="outer-box">
        <div className="picture-view">
          <h2 className="panel-title">Profile Name</h2>
        </div>
        <div className="edit-view">
          <h2 className="panel-title">My Account</h2>

          {/* Profile Information Section with Dropdowns */}
          <div style={{ width: '100%', padding: '1em' }}>
            <ProfileSection title="Profile Info">
              <div>
                <label htmlFor="name">Name:</label>
                <input
                  id="name"
                  type="text"
                  value={profileName}
                  onChange={(e) => setProfileName(e.target.value)}
                  placeholder="Enter your name"
                  className="input-field"
                />
              </div>
              <div>
                <label htmlFor="email">Email:</label>
                <input
                  id="email"
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  className="input-field"
                />
              </div>
            </ProfileSection>

            <ProfileSection title="Demographics">
              <div>
                <label htmlFor="major">Major:</label>
                <input
                  id="major"
                  type="text"
                  value={major}
                  onChange={(e) => setMajor(e.target.value)}
                  placeholder="Enter your major"
                  className="input-field"
                />
              </div>
            </ProfileSection>

            <ProfileSection title="Preferences">
              <textarea
                placeholder="Enter your preferences here..."
                className="input-field"
              />
            </ProfileSection>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Profile;
