import React, { useState } from 'react';

function GoogleCalendarEmbed() {
  const [email, setEmail] = useState('');
  const [calendarSrc, setCalendarSrc] = useState('');
  const [showError, setShowError] = useState(false);

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const showCal = () => {
    // Validate the email format
    if (email && /.+\@.+/.test(email)) {
      // Construct the Google Calendar embed URL
      setCalendarSrc(`https://calendar.google.com/calendar/embed?src=${encodeURIComponent(email)}`);
      setShowError(false);
    } else {
      // If email is invalid, show error
      setShowError(true);
      setCalendarSrc('');  // Hide the calendar
    }
  };

  return (
    <div>
      <h2>Embed Google Calendar</h2>

      {/* Email input */}
      <input
        type="email"
        value={email}
        onChange={handleEmailChange}
        placeholder="Enter Google email address"
      />
      <button onClick={showCal}>Show Calendar</button>

      {/* Error message */}
      {showError && <div style={{ color: 'red', fontWeight: 'bold' }}>That doesn't look like a valid email...</div>}

      {/* Google Calendar iframe */}
      {calendarSrc && (
        <iframe
          src={calendarSrc}
          width="800"
          height="600"
          frameBorder="0"
          style={{ marginTop: '20px' }}
          title="Google Calendar"
        ></iframe>
      )}
    </div>
  );
}

export default GoogleCalendarEmbed;
